import React, { Component } from 'react';
import './index.css';

//创建Context对象
const MyContext = React.createContext();
const { Provider } = MyContext;
export default class A extends Component {
	state = { name: 'anna' };
	render() {
		const { name } = this.state;
		return (
			<div className="parent">
				<h3>A组件</h3>
				<h4>名字是{name}</h4>
				<Provider value={name}>
					<B />
				</Provider>
			</div>
		);
	}
}
class B extends Component {
	render() {
		return (
			<div className="child">
				<h3>B组件</h3>
				<h4>从A中接收到的名字是</h4>
				<C />
			</div>
		);
	}
}
class C extends Component {
	static contextType = MyContext;//声明接收con
	render() {
		return (
			<div className="grand">
				<h3>C组件</h3>
				<h4>从A中接收到的名字是{this.context}</h4>
			</div>
		);
	}
}
