import React, { Component } from 'react';

export default class A extends Component {
	state = { name: 'anna' };
	render() {
		return (
			<div>
				<h3>A组件</h3>
				<h4>名字是{this.state.name}</h4>
				<B />
			</div>
		);
	}
}
class B extends Component {
	render() {
		return (
			<div>
				<h3>B组件</h3>
				<h4>从A中接收到的名字是{this.state.name}</h4>
				<C />
			</div>
		);
	}
}
class C extends Component {
	render() {
		return (
			<div>
				<h3>C组件</h3>
				<h4>从A中接收到的名字是{this.state.name}</h4>
				<C />
			</div>
		);
	}
}
