//函数式组件
function Demo() {
	const [count, setCount] = React.useState(0);

	React.useEffect(() => {
		setInterval(() => {
			setCount((count) => count + 1);
		}, 1000);
	}, []);

	//加的回调
	function add() {
		// setCount(count + 1);//第一种写法
		setCount((count) => count + 1);
	}

	return (
		<div>
			<h2>当前求和为：{count}</h2>
			<button onClick={add}>点击加1</button>
		</div>
	);
}

export default Demo;
