import React, { useContext, useState } from 'react';
import classes from './Cart.module.css';
import iconImg from '../../asset/bag.png';
import CartContext from '../../store/cart-context';
import CartDetails from './CartDetails/CartDetails';


const Cart = () => {
	const ctx = useContext(CartContext);

	//添加一个state，来控制详情是否显示
	const [showDetails, setShowDetails] = useState(false);

	//添加一个显示详情页的函数
	const toggleDetailsHandler = () => {
		if (ctx.totalAmount === 0) return;

		setShowDetails((preState) => !preState);
	};

	return (
		<div className={classes.Cart} onClick={toggleDetailsHandler}>
			{/* 引入购物车详情 */}
			{showDetails && <CartDetails />}

			<div className={classes.Icon}>
				<img src={iconImg} alt="bag" />
				{ctx.totalAmount === 0 ? null : (
					<span className={classes.TotalAmount}>{ctx.totalAmount}</span>
				)}
			</div>
			{ctx.totalAmount === 0 ?<p className={classes.NoMeal}>未选购商品</p>: (
				<p className={classes.Price}>{ctx.totalPrice}</p>
			)}
			<button className={`${classes.Button} ${classes.Di}`}>去结算</button>
		</div>
	);
};

export default Cart;
