// 引入Count的UI组件
import CountUI from '../../components/Count';
import { createIncrementAction } from '../../redux/count_action';

// 引入connect用于连接UI组件和redux
import { connect } from 'react-redux';

// mapStateToProps函数的返回的对象中的key就作为传递给UI组件props的key，value就作为传递给UI组件props的value ===> 传递状态
function mapStateToProps(state) {
	return { count: state };
}
//m函数的返回的对象中的key就作为传递给UI组件props的key，value就作为传递给UI组件props的value ===> 传递操作状态的方法
function m(dispatch) {
	return {
		jia: (number) => {
			//通知redux执行加法
			dispatch(createIncrementAction(number));
		},
	};
}

//使用connect()() 创建并暴露一个Count的容器组件
export default connect(mapStateToProps, m)(CountUI);
